#' Continuous Norming
#'
#' 
#' @param data the data as a data frame
#' @param raw a string specifying the raw score variable in \code{data}
#' @param group a string specifying the grouping variable in \code{data}
#' @param scale \code{'T'}, \code{'IQ'}, 'z', 'Wechsler subtest scale (m = 10,
#'   sd = 3)', or 'PISA (m = 500, sd = 100)'`
#' @param k .
#' @param selectionType .
#' @param terms a number of terms in the regression function
#' @param descend .
#' @param model .
#' @param normAge a number specifying the age for the norm score table
#' @param range .
#' @param minRaw .
#' @param maxRaw .
#' @param stepping .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$norms} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$model} \tab \tab \tab \tab \tab a html \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$norms$asDF}
#'
#' \code{as.data.frame(results$norms)}
#'
#' @export
continuous <- function(
    data,
    raw,
    group,
    scale = "T",
    k = "Quartic",
    selectionType,
    terms = 4,
    descend = FALSE,
    model = FALSE,
    normAge,
    range = 3,
    minRaw = 0,
    maxRaw = 0,
    stepping = 1) {

    if ( ! requireNamespace('jmvcore'))
        stop('continuous requires jmvcore to be installed (restart may be required)')

    if ( ! missing(raw)) raw <- jmvcore::resolveQuo(jmvcore::enquo(raw))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(raw), raw, NULL),
            `if`( ! missing(group), group, NULL))


    options <- continuousOptions$new(
        raw = raw,
        group = group,
        scale = scale,
        k = k,
        selectionType = selectionType,
        terms = terms,
        descend = descend,
        model = model,
        normAge = normAge,
        range = range,
        minRaw = minRaw,
        maxRaw = maxRaw,
        stepping = stepping)

    analysis <- continuousClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
